package com.example.fragment;import java.util.ArrayList;import java.util.List;import android.annotation.SuppressLint;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v4.app.Fragment;import android.support.v4.app.FragmentManager;import android.support.v4.app.FragmentStatePagerAdapter;import android.support.v4.view.ViewPager;import android.support.v4.view.ViewPager.OnPageChangeListener;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.RadioButton;import android.widget.RadioGroup;import android.widget.RadioGroup.OnCheckedChangeListener;import com.example.q31.R;import com.nostra13.universalimageloader.core.ImageLoader;public class Fragment2 extends Fragment{	protected ImageLoader imageLoader = ImageLoader.getInstance();	private View parentView;	private Fragment53 fa;	private Fragment53 fb;	private Fragment53 fc;	private ArrayList<Fragment> list;	private ViewPager vp;	private RadioGroup rg1;	private RadioButton rb1;	private RadioButton rb2;	private RadioButton rb3;    	@SuppressWarnings("deprecation")	@Override	@Nullable	public View onCreateView(LayoutInflater inflater,			@Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {		parentView = inflater.inflate(R.layout.fragment2, container, false);		rg1 = (RadioGroup) parentView.findViewById(R.id.rg1);		rb1 = (RadioButton) parentView.findViewById(R.id.rb1);		rb2 = (RadioButton) parentView.findViewById(R.id.rb2);		rb3 = (RadioButton) parentView.findViewById(R.id.rb3);		vp=(ViewPager)parentView.findViewById(R.id.pager);	    vp.setOnPageChangeListener(new OnPageChangeListener() {						@Override			public void onPageSelected(int arg0) {				if (arg0==0) {		             rb1.setChecked(true);				}				if (arg0==1) {		             rb2.setChecked(true);				}				if (arg0==2) {		             rb3.setChecked(true);				}							}						@Override			public void onPageScrolled(int arg0, float arg1, int arg2) {			}						@Override			public void onPageScrollStateChanged(int arg0) {							}		});		list = new ArrayList<Fragment>();		 fa=new Fragment53();		 fb=new Fragment53();		 fc=new Fragment53();		list.add(fa);		list.add(fb);		list.add(fc);        		ZxzcAdapter zxzc = new ZxzcAdapter(getChildFragmentManager(), list);		vp.setAdapter(zxzc);		zxzc.notifyDataSetChanged();		rg1.setOnCheckedChangeListener(new OnCheckedChangeListener() {			@Override			public void onCheckedChanged(RadioGroup group, int cheakedId) {				if (cheakedId == rb1.getId()) {					vp.setCurrentItem(0);				} else if (cheakedId == rb2.getId()) {					vp.setCurrentItem(1);				} else if (cheakedId == rb3.getId()) {					vp.setCurrentItem(2);				}										}		});		rb1.setChecked(true);		vp.setCurrentItem(0);		return parentView;	}	    class ZxzcAdapter extends FragmentStatePagerAdapter {		   				List<Fragment> list;					public ZxzcAdapter(FragmentManager fm,List<Fragment> list) {			super(fm);			this.list=list;					}		@SuppressLint("ResourceAsColor")		@Override		public Fragment getItem(int arg0) {				/*	if(arg0==0){				return new FragmentLogin();			}else {				return new FragmentRegister();			}*/						return list.get(arg0);								}		@Override		public int getCount() {					return list.size();		}	}}